section.script:
function SearchOrder() {


var input, filter, ul, li, a, b, i, txtValue;
input = document.getElementById("orderSearch");
filter = input.value.toUpperCase();
li = $('.all-order-list-contents').find('.table_order_kanban');
for (i = 0; i < li.length; i++) {
b = li[i].getElementsByTagName("div")[1];
a = b.getElementsByTagName("h3");
txtValue = a[0].innerHTML || a.innerText;
if (txtValue.toUpperCase().indexOf(filter) > -1) {
li[i].style.display = "";
} else {
li[i].style.display = "none";
}
}


var input_pos, filter_pos, ul_pos, li_pos, a_pos, b_pos, i_pos, txtValue_pos;
input_pos = document.getElementById("orderSearch");
filter_pos = input_pos.value.toUpperCase();
var ul_pos = document.getElementById("pos-order-list");
li_pos = $('#pos-order-list').find('.table_order_kanban');
for (i = 0; i < li_pos.length; i++) {
b_pos = li_pos[i].getElementsByTagName("div")[1];
a_pos = b_pos.getElementsByTagName("h3");
txtValue_pos = a_pos[0].innerHTML || a_pos.innerText;
if (txtValue_pos.toUpperCase().indexOf(filter) > -1) {
li_pos[i].style.display = "";
} else {
li_pos[i].style.display = "none";
}
}


var input_sale, filter_sale, ul_sale, li_sale, a_sale, b_sale, i_sale, txtValue_sale;
input_sale = document.getElementById("orderSearch");
filter_sale = input_sale.value.toUpperCase();
var ul_sale = document.getElementById("sale-order-list");
li_sale = $('.sale-order-list').find('.table_order_kanban');
for (i = 0; i < li_sale.length; i++) {
b_sale = li_sale[i].getElementsByTagName("div")[1];
a_sale = b_sale.getElementsByTagName("h3");
txtValue_sale = a_sale[0].innerHTML || a_sale.innerText;
if (txtValue_sale.toUpperCase().indexOf(filter) > -1) {
li_sale[i].style.display = "";
} else {
li_sale[i].style.display = "none";
}
}
}
